const myfile=document.getElementById("folder");function dibujar(myfile){const tree=document.getElementById("treeFiles"),ul=document.createElement("ul");ul.id="treeUl";for(let i=0;i<myfile.length;i++){const element=myfile[i],li=document.createElement("li"),span=document.createElement("span");span.innerHTML=element.name,li.appendChild(span),ul.appendChild(li),tree.appendChild(ul)}}myfile.addEventListener("change",(function(e){dibujar(myfile.files),myfile.value=""})),myfile.addEventListener("click",(function(){const tree=document.getElementById("treeFiles"),ul=document.getElementById("treeUl");tree.removeChild(ul)}));